import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useAuth } from '../context/AuthContext';

const DashboardPage = () => {
  const { user, isAdmin } = useAuth();
  const [selectedStatus, setSelectedStatus] = useState('all');
  const [editingIssue, setEditingIssue] = useState(null);

  // Mock data for demonstration
  const issues = [
    {
      id: 1,
      category: 'electricity',
      title: 'Power Outage in Library',
      location: 'Library, Floor 2',
      description: 'Complete power outage affecting study areas and computer stations.',
      status: 'pending',
      date: '2024-01-15',
      priority: 'high',
      icon: 'âš¡',
      reportedBy: 'user-001',
      reporterName: 'John Doe'
    },
    {
      id: 2,
      category: 'wifi',
      title: 'Slow WiFi in Dormitory',
      location: 'Dormitory Building A',
      description: 'Extremely slow internet connection in rooms 101-120.',
      status: 'in-progress',
      date: '2024-01-14',
      priority: 'medium',
      icon: 'ðŸ“¶',
      reportedBy: 'user-002',
      reporterName: 'Jane Smith'
    },
    {
      id: 3,
      category: 'water',
      title: 'Leaking Faucet',
      location: 'Science Building, Lab 3',
      description: 'Faucet in the chemistry lab is leaking continuously.',
      status: 'resolved',
      date: '2024-01-13',
      priority: 'low',
      icon: 'ðŸ’§',
      reportedBy: 'user-001',
      reporterName: 'John Doe'
    },
    {
      id: 4,
      category: 'cleanliness',
      title: 'Dirty Cafeteria Tables',
      location: 'Student Center Cafeteria',
      description: 'Multiple tables have food stains and need cleaning.',
      status: 'pending',
      date: '2024-01-15',
      priority: 'medium',
      icon: 'ðŸ§¹',
      reportedBy: 'user-003',
      reporterName: 'Mike Johnson'
    },
    {
      id: 5,
      category: 'other',
      title: 'Broken Chair',
      location: 'Lecture Hall 101',
      description: 'One of the chairs in the front row is broken and unsafe.',
      status: 'in-progress',
      date: '2024-01-12',
      priority: 'high',
      icon: 'ðŸ”§',
      reportedBy: 'user-001',
      reporterName: 'John Doe'
    },
    {
      id: 6,
      category: 'electricity',
      title: 'Faulty Light Switch',
      location: 'Engineering Building, Room 205',
      description: 'Light switch not working properly, lights flicker when turned on.',
      status: 'resolved',
      date: '2024-01-11',
      priority: 'low',
      icon: 'âš¡',
      reportedBy: 'user-004',
      reporterName: 'Sarah Wilson'
    }
  ];

  // Filter issues based on user role
  const userIssues = isAdmin() ? issues : issues.filter(issue => issue.reportedBy === user?.id);
  
  const statusFilters = [
    { value: 'all', label: 'All Issues', count: userIssues.length },
    { value: 'pending', label: 'Pending', count: userIssues.filter(i => i.status === 'pending').length },
    { value: 'in-progress', label: 'In Progress', count: userIssues.filter(i => i.status === 'in-progress').length },
    { value: 'resolved', label: 'Resolved', count: userIssues.filter(i => i.status === 'resolved').length }
  ];

  const filteredIssues = selectedStatus === 'all' 
    ? userIssues 
    : userIssues.filter(issue => issue.status === selectedStatus);

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'in-progress': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'resolved': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending': return 'â³';
      case 'in-progress': return 'ðŸ”„';
      case 'resolved': return 'âœ…';
      default: return 'â“';
    }
  };

  const handleStatusChange = (issueId, newStatus) => {
    // In a real app, this would update the database
    console.log(`Changing issue ${issueId} status to ${newStatus}`);
  };

  const handleDeleteIssue = (issueId) => {
    // In a real app, this would delete from database
    console.log(`Deleting issue ${issueId}`);
  };

  const handleEditIssue = (issue) => {
    setEditingIssue(issue);
  };

  return (
    <div className="min-h-screen py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="text-center mb-12"
        >
          <h1 className="text-4xl font-bold text-gray-900 mb-4">
            {isAdmin() ? 'Admin Dashboard' : 'My Issues'}
          </h1>
          <p className="text-lg text-gray-600 max-w-2xl mx-auto">
            {isAdmin() 
              ? 'Manage and track all reported facility issues across the campus'
              : 'Track and manage your reported facility issues'
            }
          </p>
        </motion.div>

        {/* Stats Cards */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8"
        >
          {statusFilters.map((filter, index) => (
            <motion.div
              key={filter.value}
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6, delay: 0.3 + index * 0.1 }}
              whileHover={{ y: -5 }}
              className="card p-6 text-center cursor-pointer"
              onClick={() => setSelectedStatus(filter.value)}
            >
              <div className="text-3xl mb-2">{getStatusIcon(filter.value === 'all' ? 'all' : filter.value)}</div>
              <div className="text-2xl font-bold text-gray-900 mb-1">{filter.count}</div>
              <div className="text-sm text-gray-600">{filter.label}</div>
            </motion.div>
          ))}
        </motion.div>

        {/* Filter Tabs */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          className="flex flex-wrap justify-center gap-2 mb-8"
        >
          {statusFilters.map((filter) => (
            <motion.button
              key={filter.value}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setSelectedStatus(filter.value)}
              className={`px-4 py-2 rounded-lg font-medium transition-all duration-300 ${
                selectedStatus === filter.value
                  ? 'bg-primary-600 text-white shadow-lg'
                  : 'bg-white text-gray-600 hover:bg-gray-50 border border-gray-200'
              }`}
            >
              {filter.label} ({filter.count})
            </motion.button>
          ))}
        </motion.div>

        {/* Issues Grid */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
        >
          <AnimatePresence mode="wait">
            {filteredIssues.map((issue, index) => (
              <motion.div
                key={issue.id}
                initial={{ opacity: 0, y: 30, scale: 0.9 }}
                animate={{ opacity: 1, y: 0, scale: 1 }}
                exit={{ opacity: 0, y: -30, scale: 0.9 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ y: -5 }}
                className="card p-6"
              >
                {/* Header */}
                <div className="flex items-start justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className="text-2xl">{issue.icon}</div>
                    <div>
                      <h3 className="font-semibold text-gray-900 text-lg">{issue.title}</h3>
                      <p className="text-sm text-gray-500">{issue.location}</p>
                      {isAdmin() && (
                        <p className="text-xs text-gray-400">Reported by: {issue.reporterName}</p>
                      )}
                    </div>
                  </div>
                  <div className="flex flex-col items-end space-y-2">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium border ${getStatusColor(issue.status)}`}>
                      {getStatusIcon(issue.status)} {issue.status.replace('-', ' ')}
                    </span>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${getPriorityColor(issue.priority)}`}>
                      {issue.priority} priority
                    </span>
                  </div>
                </div>

                {/* Description */}
                <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                  {issue.description}
                </p>

                {/* Footer */}
                <div className="flex items-center justify-between text-xs text-gray-500">
                  <span>Reported: {new Date(issue.date).toLocaleDateString()}</span>
                  <span>ID: #{issue.id}</span>
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-2 mt-4">
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    className="flex-1 px-3 py-2 bg-primary-50 text-primary-600 rounded-lg text-sm font-medium hover:bg-primary-100 transition-colors duration-300"
                  >
                    View Details
                  </motion.button>
                  
                  {isAdmin() && (
                    <>
                      {/* Admin Actions */}
                      <motion.button
                        whileHover={{ scale: 1.05 }}
                        whileTap={{ scale: 0.95 }}
                        onClick={() => handleEditIssue(issue)}
                        className="px-3 py-2 bg-blue-50 text-blue-600 rounded-lg text-sm font-medium hover:bg-blue-100 transition-colors duration-300"
                      >
                        Edit
                      </motion.button>
                      
                      {issue.status === 'pending' && (
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => handleStatusChange(issue.id, 'in-progress')}
                          className="px-3 py-2 bg-green-50 text-green-600 rounded-lg text-sm font-medium hover:bg-green-100 transition-colors duration-300"
                        >
                          Start Work
                        </motion.button>
                      )}
                      
                      {issue.status === 'in-progress' && (
                        <motion.button
                          whileHover={{ scale: 1.05 }}
                          whileTap={{ scale: 0.95 }}
                          onClick={() => handleStatusChange(issue.id, 'resolved')}
                          className="px-3 py-2 bg-green-50 text-green-600 rounded-lg text-sm font-medium hover:bg-green-100 transition-colors duration-300"
                        >
                          Mark Resolved
                        </motion.button>
                      )}
                    </>
                  )}
                  
                  {/* User can delete their own issues */}
                  {!isAdmin() && (
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => handleDeleteIssue(issue.id)}
                      className="px-3 py-2 bg-red-50 text-red-600 rounded-lg text-sm font-medium hover:bg-red-100 transition-colors duration-300"
                    >
                      Delete
                    </motion.button>
                  )}
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </motion.div>

        {/* Empty State */}
        {filteredIssues.length === 0 && (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            className="text-center py-12"
          >
            <div className="text-6xl mb-4">ðŸ“‹</div>
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No issues found</h3>
            <p className="text-gray-600">
              {isAdmin() 
                ? 'There are no issues matching the selected filter.'
                : 'You haven\'t reported any issues yet.'
              }
            </p>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
